import mc from "./app.module.scss";
import React from "react";
import NavBarHeader from "../nav-bar-header/NavBarHeader";
import Main from "../main/Main";
import Login from "../login/Login";
import { useDispatch, useSelector } from "react-redux";
import NavBarLeft from "../nav-bar-left/NavBarLeft";

const App = () => {
  const dispatch = useDispatch();

  const handleClickCloseLogin = () => {
    const action = {
      type: "CLOSE_LOGIN",
    };
    dispatch(action);
  };

  const { modificator } = useSelector((store) => {
    return { modificator: store.modificatorsReducer };
  });

  const { user } = useSelector((store) => {
    return { user: store.userReducer };
  });

  useEffect(() => {
    const token = getItem("token");

    console.log(token);

    // if (token === null || token.length === 0) {
    //   setIsLogged(false);
    // } else {
    //   setIsLogged(true);
    // }

    // token === null || token.length < 0 ? setIsLogged(false) : setIsLogged(true);

    // setIsLogged(token !== null && token.length > 0);

    // setIsLogged(token && token.length > 0);

    setIsLogged(!!token?.trim());
    // si token = null > false
    // si token = "" > false
    // si token = " " > false
  }, []);

  return (
    <>
      <Login />
      <div
        className={
          modificator.loginView
            ? `${mc.container} ${mc.loginActive}`
            : mc.container
        }
      >
        <div className={mc.nav_bar_header}>
          <NavBarHeader />
        </div>
        <div className={mc.main} onClick={handleClickCloseLogin}>
          <NavBarLeft />
          <Main />
        </div>
      </div>
    </>
  );
};

export default App;
