import mc from "./fight.module.scss";
//images//
import rogueImgP1 from "../../../images/rogueImgP1.png";
import mageImgP1 from "../../../images/mageImgP1.png";
import warriorImgP1 from "../../../images/warriorImgP1.png";

import rogueImgP2 from "../../../images/rogueImgP2.png";
import mageImgP2 from "../../../images/mageImgP2.png";
import warriorImgP2 from "../../../images/warriorImgP2.png";

const Fight = ({ player1, player2 }) => {
  return (
    <div className={mc.container}>
      <div className={`${mc.player1} adaptive-img-contain`}>
        <div className={`${mc.player1} adaptive-img-contain`}>
          <span>
            <img
              src={
                player1.playerClass.className === "voleur"
                  ? rogueImgP1
                  : player1.playerClass.className === "mage"
                  ? mageImgP1
                  : warriorImgP1
              }
              alt={`{image ${player1.playerClass.className}`}
            />
          </span>
        </div>
      </div>
      <div className={mc.logs}></div>
      <div className={`${mc.player2} adaptive-img-contain`}>
        <span>
          <img
            src={
              player2.playerClass.className === "voleur"
                ? rogueImgP2
                : player2.playerClass.className === "mage"
                ? mageImgP2
                : warriorImgP2
            }
            alt={`{image du ${player2.playerClass.className}`}
          />
        </span>
      </div>
    </div>
  );
};

export default Fight;
