const INITIAL_SIGNUP = {
  id: 1,
  pseudo: {
    value: "",
    selected: false,
  },
  email: {
    value: "",
    selected: false,
  },
  confirmEmail: {
    value: "",
    selected: false,
  },
  password: {
    value: "",
    selected: false,
  },
  confirmPassword: {
    value: "",
    selected: false,
  },
};

const signUpReducer = (state = INITIAL_SIGNUP, action) => {
  switch (action.type) {
    case "IS_LOGGED": {
      return {
        ...state,
        isLogged: true,
      };
    }

    case "UPDATE_SIGNUP_FIELD": {
      return {
        ...state,
        [action.payload.key]: {
          ...state[action.payload.key],
          value: action.payload.value,
        },
      };
    }

    case "CLEAN_SIGNUP_INPUTS": {
      return {
        ...state,
        pseudo: {
          value: "",
        },
        email: {
          value: "",
        },
        confirmEmail: {
          value: "",
        },
        password: {
          value: "",
        },
        confirmPassword: {
          value: "",
        },
      };
    }

    case "LABEL_TOP": {
      return {
        ...state,
        [action.payload.input]: {
          ...state[action.payload.input],
          selected: true,
        },
      };
    }

    case "LABEL_DOWN": {
      return {
        ...state,
        pseudo: {
          selected: false,
        },
        email: {
          selected: false,
        },
        confirmEmail: {
          selected: false,
        },
        password: {
          selected: false,
        },
        confirmPassword: {
          selected: false,
        },
      };
    }

    default: {
      return state;
    }
  }
};

export default signUpReducer;
