import { useState } from "react";
import mc from "./sign-in.module.scss";
import { useDispatch, useSelector } from "react-redux";

const SignIn = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((store) => {
    return { user: store.userReducer };
  });

  const handleInputChange = (e, input) => {
    console.log(user);
    const action = {
      type: "UPDATE_INPUT",
      payload: {
        value: e.target.value,
        input: input,
      },
    };
    dispatch(action);
  };

  const handleInputClick = (e, input) => {
    const action = {
      type: "LABEL_TOP",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const handleXClick = () => {
    const action = {
      type: "CLOSE_LOGIN",
    };
    dispatch(action);
  };

  const handleSubmit = (e) => {
    e.preventDefault();
  };

  return (
    <div className={mc.container}>
      <div className={mc.login_close}>
        <button onClick={handleXClick}>X</button>
      </div>
      <div>
        <p className={mc.title}>Connection</p>
        <form onSubmit={(e) => handleSubmit(e)}>
          <div className={mc.inputs}>
            <div className={mc.inputs_blocks}>
              <label className={user.email.selected ? mc.label_up : {}}>
                Email
              </label>
              <input
                required={true}
                type="email"
                value={user.email.value}
                onChange={(e) => handleInputChange(e, "email")}
                onClick={(e) => handleInputClick(e, "email")}
              ></input>
            </div>
            <div className={mc.inputs_blocks}>
              <label className={user.password.selected ? mc.label_up : {}}>
                Password
              </label>
              <input
                required={true}
                type="password"
                value={user.password.value}
                onChange={(e) => handleInputChange(e, "password")}
                onClick={(e) => handleInputClick(e, "password")}
              ></input>
            </div>
          </div>
          <div className={mc.connect}>
            <button type="submit" onClick={(e) => handleSubmit(e)}>
              Se connecter
            </button>
            <span>S'incrire</span>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignIn;
