import mc from "./fight-scene.module.scss";
import { useDispatch, useSelector } from "react-redux";
//components//
import LifeBar from "../fight-scene-elements/life-bar/LifeBar";

const FightScene = () => {
  const dispatch = useDispatch();

  //launch switch page animation//
  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      dispatch({ type: `FIGHT_END` });
    }, 300);
  };

  return (
    <div onClick={onEndFightButtonClick} className={mc.container}>
      <div className={mc.fightingZone_zone}>
        <div className={mc.top_menu}>
          <div className={mc.life_bars_player1}>
            <LifeBar
            // playerLife={}
            />
          </div>
          <div className={mc.round_rotation}></div>
          <div className={mc.life_bars_player2}>
            <LifeBar
            // playerLife={}
            />
          </div>
        </div>
        <div className={mc.mdj_menu}></div>
        <div className={mc.fighting_menu}></div>
      </div>
    </div>
  );
};

export default FightScene;
