import mc from "./fight-scene.module.scss";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useRef } from "react";
//sprites//
import zombie_player1 from "../../images/magzombie_player1eP1.png";
import zombie_player2 from "../../images/zombie_player2.png";
import knight_player1 from "../../images/knight_player1.png";
import knight_player2 from "../../images/knight_player2.png";
import ninja_player1 from "../../images/ninja_player1.png";
import ninja_player2 from "../../images/ninja_player2.png";

const FightScene = () => {
  // const [x, setX] = useState(); // Coordonnée X de l'image
  // const canvasRef = useRef(null);

  // useEffect(() => {
  //   const canvas = canvasRef.current;
  //   const context = canvas.getContext("2d");
  //   const image = new Image();
  //   image.src = "chemin/vers/votre/image.png";
  //   const imageWidth = 100;
  //   const imageHeight = 100;
  //   const y = canvas.height / 2;

  //   setX(canvas.width / 2 - imageWidth / 2); // Coordonnée de départ

  //   function animate() {
  //     context.clearRect(0, 0, canvas.width, canvas.height);
  //     context.drawImage(image, x, y, imageWidth, imageHeight);
  //     setX((prevX) => prevX + 1); // Mise à jour de la coordonnée X

  //     requestAnimationFrame(animate);
  //   }

  //   animate();
  // }, []);

  const dispatch = useDispatch();

  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      //setTimeout to match with transition on css and let animation finish before components switch
      dispatch({ type: `FIGHT_END` });
    }, 300);
  };

  const canvasRef = useRef(null);

  useEffect(() => {
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.fillStyle = "black";
    context.fillRect(0, 0, canvas.width, canvas.height);
  }, []);

  useEffect(() => {
    const x = 0; // Coordonnée X de l'image sur le canvas
    const y = 0; // Coordonnée Y de l'image sur le canvas
    const width = 100; // Largeur de l'image sur le canvas
    const height = 100; // Hauteur de l'image sur le canvas
    const canvas = canvasRef.current;
    const context = canvas.getContext("2d");
    context.clearRect(0, 0, canvas.width, canvas.height); // Efface le canvas
    context.fillStyle = "black";
    context.fillRect(0, 0, canvas.width, canvas.height);
    // Charge l'image
    const image = new Image();
    image.src = ninja_player1;

    // Attend que l'image soit chargée avant de la dessiner
    image.onload = () => {
      // Dessine l'image sur le canvas
      context.drawImage(image, x, y, width, height); // Remplacez x, y, width, height par les valeurs appropriées
    };
  }, []);

  return (
    <div onClick={onEndFightButtonClick} className={mc.container}>
      <canvas ref={canvasRef} className={mc.canvas_game} />
    </div>
  );
};

export default FightScene;
