const INITIAL_STATE = {
  pseudoPlayer1Validate: false,
  pseudoPlayer2Validate: false,
  inputP1ValidationError: false,
  inputP2ValidationError: false,
  classSelectedP1: false,
  classSelectedP2: false,
};

const animationsReducer = (state = INITIAL_STATE, action) => {
  switch (action.type) {
    case "PLAYER1_PSEUDO_VALIDATE": {
      return { ...state, pseudoPlayer1Validate: true };
    }

    default: {
      return state;
    }
  }
};

export default animationsReducer;
