import mc from "./fight-scene.module.scss";
import { useSelector, useDispatch } from "react-redux";
import { useEffect, useRef, useState } from "react";
//sprites//
import zombie_player1 from "../../images/zombie_player1.png";
import zombie_player2 from "../../images/zombie_player2.png";
import knight_player1 from "../../images/knight_player1.png";
import knight_player2 from "../../images/knight_player2.png";
import ninja_player1 from "../../images/ninja_player1.png";
import ninja_player2 from "../../images/ninja_player2.png";

const FightScene = () => {
  const dispatch = useDispatch();

  const onEndFightButtonClick = () => {
    dispatch({ type: `ANIMATION_FIGHT_START_CANCEL` });
    setTimeout(() => {
      //setTimeout to match with transition on css and let animation finish before components switch
      dispatch({ type: `FIGHT_END` });
    }, 300);
  };

  const canvasRef = useRef(null);
  const [ctx, setCtx] = useState(null);

  //   ctx.drawImage(
  //     image,      // L'objet Image à dessiner (préchargé avec img.onload)
  //     sourceX,    // La position horizontale dans l'image source où commencer à dessiner
  //     sourceY,    // La position verticale dans l'image source où commencer à dessiner
  //     sourceWidth,// La largeur de la portion d'image source à utiliser
  //     sourceHeight,// La hauteur de la portion d'image source à utiliser
  //     destinationX, // La position horizontale dans le canvas où commencer à dessiner
  //     destinationY, // La position verticale dans le canvas où commencer à dessiner
  //     destinationWidth, // La largeur à laquelle dessiner l'image dans le canvas (redimensionnement)
  //     destinationHeight // La hauteur à laquelle dessiner l'image dans le canvas (redimensionnement)
  //   );

  const addImage = (
    context,
    image,
    posX,
    posY,
    sWidth,
    sHeight,
    x,
    y,
    width,
    height
  ) => {
    const myImg = new Image();
    myImg.src = image;
    myImg.onload = () => {
      context.drawImage(
        myImg,
        posX,
        posY,
        sWidth,
        sHeight,
        x,
        y,
        width,
        height
      );
    };
  };

  useEffect(() => {
    const canvas = canvasRef.current;
    const ctx = canvas.getContext("2d");
    setCtx(ctx); // Store the context in state
    ctx.fillStyle = "black";
    ctx.fillRect(0, 0, canvas.width, canvas.height);
    // const img = new Image();
    // img.onload = () => {
    //   ctx.drawImage(img, 0, 0, 100, 100); // Dessine l'image aux coordonnées (x, y)
    // };
    // img.src = zombie_player1;
    addImage(ctx, zombie_player1);
  }, []);

  return (
    <div onClick={onEndFightButtonClick} className={mc.container}>
      <canvas ref={canvasRef} className={mc.canvas_game} />
    </div>
  );
};

export default FightScene;
