import mc from "./app.module.scss";

import { useDispatch, useSelector } from "react-redux";
import { useEffect, useState } from "react";
import { getItem } from "../../utils/storage.utils";
import { cleanInputsAndLabels } from "../../utils/login-inputs.utils.js";

import NavBarHeader from "../nav-bar-header/NavBarHeader";
import Main from "../main/Main";
import Login from "../login/Login";
import NavBarLeft from "../nav-bar-left/NavBarLeft";

const App = () => {
  const dispatch = useDispatch();
  const [isLogged, setIsLogged] = useState(false);

  const handleClickCloseLogin = () => {
    dispatch({ type: "CLOSE_LOGIN" });
    dispatch({ type: "CLEAN_SIGNIN_INPUTS" });
    dispatch({ type: "LABEL_SIGNIN_DOWN" });
    dispatch({ type: "LABEL_SIGNUP_INPUTS" });
    dispatch({ type: "LABEL_SIGNUP_DOWN" });
  };

  const { modificator } = useSelector((store) => {
    return { modificator: store.modificatorsReducer };
  });

  // useEffect(() => {
  //   const token = getItem("token");
  //   console.log(token);
  //   setIsLogged(!!token?.trim());
  // }, []);

  return (
    <>
      <Login />
      <div
        className={
          modificator.loginView
            ? `${mc.container} ${mc.loginActive}`
            : mc.container
        }
      >
        <div className={mc.nav_bar_header}>
          <NavBarHeader />
        </div>
        <div className={mc.main} onClick={handleClickCloseLogin}>
          <NavBarLeft />
          <Main />
        </div>
      </div>
    </>
  );
};

export default App;
