import mc from "./players-menu.module.scss";
import { useSelector, useDispatch } from "react-redux";
//components//
import PseudoInputs from "../inputs/pseudoInputs/PseudoInputs";

const PlayersMenu = () => {
  const dispatch = useDispatch();

  const { player1, player2, pseudoPlayer1 } = useSelector((store) => {
    return {
      player1: store.playersReducer.player1,
      player2: store.playersReducer.player2,
      pseudoPlayer1: store.modificatorsReducer.pseudoPlayer1,
      pseudoPlayer2: store.modificatorsReducer.pseudoPlayer2,
    };
  });

  return (
    <div className={mc.container}>
      <div className={mc.players_pseudo}>
        <div className={mc.player1}>
          <PseudoInputs
            labelContent="Pseudo"
            inputValue={player1.pseudo}
            player="player1"
            pseudoP1P2="Player1"
            // enterValidation={(e) => }
          />
        </div>
        <div className={mc.player2}>
          <PseudoInputs
            labelContent="Pseudo"
            inputValue={player2.pseudo}
            player="player2"
            pseudoP1P2="Player2"
          />
        </div>
      </div>
    </div>
  );
};

export default PlayersMenu;
