import { useState } from "react";
import mc from "./sign-in.module.scss";
import { useDispatch, useSelector } from "react-redux";
import { setItem } from "../../../utils/storage.utils";

const SignIn = () => {
  const dispatch = useDispatch();
  const { user } = useSelector((store) => {
    return { user: store.userReducer };
  });

  const handleInputChange = (e, input) => {
    const action = {
      type: "UPDATE_INPUT",
      payload: {
        value: e.target.value,
        input: input,
      },
    };
    dispatch(action);
  };

  const cleanInput = (input) => {
    const action = {
      type: "CLEAN_INPUTS_ERROR",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const labelUp = (e, input) => {
    const action = {
      type: "LABEL_TOP",
      payload: {
        input: input,
      },
    };
    dispatch(action);
  };

  const closeLogin = () => {
    const action = {
      type: "CLOSE_LOGIN",
    };
    dispatch(action);
  };

  const openSignUp = () => {
    const action = {
      type: "switchSignUp",
    };
    dispatch(action);
  };

  const handleSubmit = async (e) => {
    e.preventDefault();
    let result;
    let status;

    const form = {
      email: user.email.value,
      password: user.password.value,
    };

    const config = {
      method: "POST",
      body: JSON.stringify(form),
      headers: {
        "content-type": "application/json; charset=UTF8",
      },
    };

    try {
      const response = await fetch(
        `http://localhost:7001/users/sign-in`,
        config
      );
      status = response.status;
      result = await response.json();
    } catch (e) {
      console.error(`Problem with request ${e.message}`);
      onError();
      return;
    }

    if (status >= 400) {
      onError(status);
      return;
    }

    cleanInput("email");
    cleanInput("password");

    const token = result.token;
    // window.localStorage.setItem("token", JSON.stringify(token));
    setItem("token", token);
    setIsLogged(true);
  };

  return (
    <div className={mc.container}>
      <div className={mc.login_close}>
        <button onClick={closeLogin}>X</button>
      </div>
      <div>
        <p className={mc.title}>Connection</p>
        <form onSubmit={(e) => handleSubmit(e)}>
          <div className={mc.inputs}>
            <div className={mc.inputs_left}>
              <div className={mc.inputs_blocks}>
                <label className={user.email.selected ? mc.label_up : {}}>
                  Email
                </label>
                <input
                  required={true}
                  type="email"
                  value={user.email.value}
                  onChange={(e) => handleInputChange(e, "email")}
                  onClick={(e) => labelUp(e, "email")}
                ></input>
              </div>
              <div className={mc.inputs_blocks}>
                <label className={user.password.selected ? mc.label_up : {}}>
                  Password
                </label>
                <input
                  required={true}
                  type="password"
                  value={user.password.value}
                  onChange={(e) => handleInputChange(e, "password")}
                  onClick={(e) => labelUp(e, "password")}
                ></input>
              </div>
            </div>
          </div>
          <div className={mc.connect}>
            <button type="submit" onClick={(e) => handleSubmit(e)}>
              Se connecter
            </button>
            <span onClick={openSignUp}>S'incrire</span>
          </div>
        </form>
      </div>
    </div>
  );
};

export default SignIn;
